[0x0000001A0E81FA08] = { id = "Items.ContagionLvl2Program", name = "CONTAGION", kind = "Quickhack", quality = "Rare", desc = "Non-Lethal. Deals low damage that applies poison to the target. Spreads to 2 target(s) within 8-meter radius. Effective against group targets. Poison lasts significantly longer.", comment = "Did you know most alloys used in cyberware manufacturing contain trace amounts of toxic elements such as lead, cadmium and mercury? And that the safeguards keeping them from slipping into your bloodstream can be broken? Well you're not alone.", },
[0x0000001AC22BFA96] = { id = "Items.ContagionLvl3Program", name = "CONTAGION", kind = "Quickhack", quality = "Epic", desc = "Non-Lethal. Deals low damage that applies poison to the target. Spreads to 2 target(s) within 8-meter radius. Effective against group targets. Poison lasts significantly longer. Each subsequent target receives 30% more damage from contagion.", comment = "Did you know most alloys used in cyberware manufacturing contain trace amounts of toxic elements such as lead, cadmium and mercury? And that the safeguards keeping them from slipping into your bloodstream can be broken? Well you're not alone.", },
[0x0000001AC8EEF38F] = { id = "Items.ContagionLvl4Program", name = "CONTAGION", kind = "Quickhack", quality = "Legendary", desc = "Non-Lethal. Deals low damage that applies poison to the target. Spreads to 2 target(s) within 8-meter radius. Effective against group targets. Poison lasts significantly longer. Each subsequent target receives 30% more damage from contagion. Passive While Equipped: All quickhacks capable of spreading to multiple targets can jump 1 additional time(s).", comment = "Did you know most alloys used in cyberware manufacturing contain trace amounts of toxic elements such as lead, cadmium and mercury? And that the safeguards keeping them from slipping into your bloodstream can be broken? Well you're not alone.", },
[0x000000221C995EEC] = { id = "Items.LocomotionMalfunctionProgram", name = "CRIPPLE MOVEMENT", kind = "Quickhack", quality = "Uncommon", desc = "Disables the target's ability to move from their current position.", comment = "Not everyone thinks to protect the cyberware in their inner ear. But it's a lesson quickly learned when you lose the ability to move and shoot in the middle of a firefight.", },
[0x00000026FFC0474C] = { id = "Items.LocomotionMalfunctionLvl2Program", name = "CRIPPLE MOVEMENT", kind = "Quickhack", quality = "Rare", desc = "Disables the target's ability to move from their current position. Spreads to the nearest enemy within 8 meter(s).", comment = "Not everyone thinks to protect the cyberware in their inner ear. But it's a lesson quickly learned when you lose the ability to move and shoot in the middle of a firefight.", },
[0x00000026336A47D2] = { id = "Items.LocomotionMalfunctionLvl3Program", name = "CRIPPLE MOVEMENT", kind = "Quickhack", quality = "Epic", desc = "Disables the target's ability to move from their current position. Spreads to the nearest enemy within 8 meter(s). Affected enemies are also unable to attack.", comment = "Not everyone thinks to protect the cyberware in their inner ear. But it's a lesson quickly learned when you lose the ability to move and shoot in the middle of a firefight.", },
[0x0000002639AF4ECB] = { id = "Items.LocomotionMalfunctionLvl4Program", name = "CRIPPLE MOVEMENT", kind = "Quickhack", quality = "Legendary", desc = "Disables the target's ability to move from their current position. Spreads to the nearest enemy within 8 meter(s). Affected enemies are also unable to attack. Passive While Equipped: enemies under the effect of any quickhack cannot sprint.", comment = "Not everyone thinks to protect the cyberware in their inner ear. But it's a lesson quickly learned when you lose the ability to move and shoot in the middle of a firefight.", },
[0x000000183F235049] = { id = "Items.MadnessLvl4Program", name = "CYBERPSYCHOSIS", kind = "Quickhack", quality = "Legendary", desc = "Leathal. Induces a cyberpyschosis-like state in the target, causing them to attack both enemies and allies. Sets the status of drones, mechs and robots to friendly, making them turn against your enemies. If no other allies are nearby, the target will commit suicide. Passive While Equipped: under the effect of any quickhack will no longer try to avoid inflicting friendly fire.", comment = "\"If cyberpsychos are an invention of the tabloids, then who was behind yesterday's massacre of 76 people at 7th Hell?\" - Op-ed excerpt, Night City Herald", },
[0x0000001DF34415D1] = { id = "Items.DisableCyberwareProgram", name = "CYBERWARE MALFUNCTION", kind = "Quickhack", quality = "Uncommon", desc = "Disables the target's cyberware abilitie. Can disable movement or Resistances. Very effective against fast-moving targets and netrunners.", comment = "What better way to discourage an enemy than by making them carry around literal pounds of dead weight?", },
[0x00000021856D41AA] = { id = "Items.DisableCyberwareLvl2Program", name = "CYBERWARE MALFUNCTION", kind = "Quickhack", quality = "Rare", desc = "Disables the target's cyberware abilitie. Can disable movement or Resistances. Very effective against fast-moving targets and netrunners. Spreads to the nearest target within 8-meter(s).", comment = "What better way to discourage an enemy than by making them carry around literal pounds of dead weight?", },
[0x0000002149C74134] = { id = "Items.DisableCyberwareLvl3Program", name = "CYBERWARE MALFUNCTION", kind = "Quickhack", quality = "Epic", desc = "Disables the target's cyberware abilitie. Can disable movement or Resistances. Very effective against fast-moving targets and netrunners. Spreads to the nearest target within 8-meter(s). Causes a random implant to explode once the effect's duration expires.", comment = "What better way to discourage an enemy than by making them carry around literal pounds of dead weight?", },
[0x0000001B5B32F50E] = { id = "Items.MemoryWipeLvl2Program", name = "MEMORY WIPE", kind = "Quickhack", quality = "Rare", desc = "Causes the target to exit combat state.", comment = "\"I call it 'white-glove hacking.' First you get your hands into the short-term memory, then you wipe sensory data to zero. Simple, elegant, leaves no trace.\" - Bam Bam Pam", },
[0x0000001B9798F590] = { id = "Items.MemoryWipeLvl3Program", name = "MEMORY WIPE", kind = "Quickhack", quality = "Epic", desc = "Causes the target to exit combat state. Affects the target's entire squad.", comment = "\"I call it 'white-glove hacking.' First you get your hands into the short-term memory, then you wipe sensory data to zero. Simple, elegant, leaves no trace.\" - Bam Bam Pam", },
[0x00000015E683AF78] = { id = "Items.OverheatProgram", name = "OVERHEAT", kind = "Quickhack", quality = "Uncommon", desc = "Non-Leathal. Applies Burn to the target, dealing heat damage over time. Less effective against drones, mechs, and robots.", comment = "When your bodily fluids begin to boil, it becomes pretty difficult to focus on much else.", },
[0x0000001994A1C981] = { id = "Items.OverheatLvl2Program", name = "OVERHEAT", kind = "Quickhack", quality = "Rare", desc = "Non-Leathal. Applies Burn to the target, dealing heat damage over time. Less effective against drones, mechs, and robots. Targets affected by Burn from Overheat are unable to preform actions.", comment = "When your bodily fluids begin to boil, it becomes pretty difficult to focus on much else.", },
[0x00000019580BC91F] = { id = "Items.OverheatLvl3Program", name = "OVERHEAT", kind = "Quickhack", quality = "Epic", desc = "Non-Leathal. Applies Burn to the target, dealing heat damage over time. Less effective against drones, mechs, and robots. Targets affected by Burn from Overheat are unable to preform actions. Burm from Overheat lasts significantly longer.", comment = "When your bodily fluids begin to boil, it becomes pretty difficult to focus on much else.", },
[0x0000001952CEC006] = { id = "Items.OverheatLvl4Program", name = "OVERHEAT", kind = "Quickhack", quality = "Legendary", desc = "Non-Leathal. Applies Burn to the target, dealing heat damage over time. Less effective against drones, mechs, and robots. Targets affected by Burn from Overheat are unable to preform actions. Burm from Overheat lasts significantly longer. Targets affected by Burn from Overheat are unable to use cyberware abilities.", comment = "When your bodily fluids begin to boil, it becomes pretty difficult to focus on much else.", },
[0x00000011991763D0] = { id = "Items.PingProgram", name = "PING", kind = "Quickhack", quality = "Uncommon", desc = "Reveals enemeis and devices connected to the local network.", comment = "Some of the oldest, most basic functions in computing may never outlive their usefulness.", },
[0x00000015515CB4FC] = { id = "Items.PingLvl2Program", name = "PING", kind = "Quickhack", quality = "Rare", desc = "Reveals enemeis and devices connected to the local network.", comment = "Some of the oldest, most basic functions in computing may never outlive their usefulness.", },
[0x000000159DF6B462] = { id = "Items.PingLvl3Program", name = "PING", kind = "Quickhack", quality = "Epic", desc = "Reveals enemeis and devices connected to the local network.", comment = "Some of the oldest, most basic functions in computing may never outlive their usefulness.", },
[0x000000159733BD7B] = { id = "Items.PingLvl4Program", name = "PING", kind = "Quickhack", quality = "Legendary", desc = "Reveals enemeis and devices connected to the local network. Highlighted enemies and devices can be scanned and quickhacked through obstacles.", comment = "Some of the oldest, most basic functions in computing may never outlive their usefulness.", },
[0x00000012BBCF9FE2] = { id = "Items.BlindProgram", name = "REBOOT OPTICS", kind = "Quickhack", quality = "Uncommon", desc = "Resets an enemy's optical cyberware, rendering them temporarily blind. Duration: 15.20 sec.", comment = "When you pay for expensive optical implants, you leave behind your worries of dry eyes, allergy irritation and pink eye. But not blindness.", },
[0x000000162B210091] = { id = "Items.BlindLvl2Program", name = "REBOOT OPTICS", kind = "Quickhack", quality = "Rare", desc = "Resets an enemy's optical cyberware, rendering them temporarily blind. Spreads to the nearest enemy withing 8 meter(s) Duration: 15.20.", comment = "When you pay for expensive optical implants, you leave behind your worries of dry eyes, allergy irritation and pink eye. But not blindness.", },
[0x00000016E78B000F] = { id = "Items.BlindLvl3Program", name = "REBOOT OPTICS", kind = "Quickhack", quality = "Epic", desc = "Resets an enemy's optical cyberware, rendering them temporarily blind. Spreads to the nearest enemy withing 8 meter(s). Greatly increases Effect Duration. Duration 22.80 sec.", comment = "When you pay for expensive optical implants, you leave behind your worries of dry eyes, allergy irritation and pink eye. But not blindness.", },
[0x00000016ED4E0916] = { id = "Items.BlindLvl4Program", name = "REBOOT OPTICS", kind = "Quickhack", quality = "Legendary", desc = "Resets an enemy's optical cyberware, rendering them temporarily blind. Spreads to the nearest enemy withing 8 meter(s). Greatly increases Effect Duration. Passive While equiped: Unlocks Optic Jammer daemon for use in Breach Protocal. Duration 22.80 sec.", comment = "When you pay for expensive optical implants, you leave behind your worries of dry eyes, allergy irritation and pink eye. But not blindness.", },
[0x0000001868968173] = { id = "Items.CommsCallInProgram", name = "REQUEST BACKUP", kind = "Quickhack", quality = "Uncommon", desc = "Calls over 1 enemy squad member(s).", comment = "One version of the program led to a political crisis in the Middle East where it was used on an entire Arasaka tank division, causing them to surround a building where a critical peace negotiations were taking place.", },
[0x0000001C806C6DC7] = { id = "Items.CommsCallInLvl3Program", name = "REQUEST BACKUP", kind = "Quickhack", quality = "Epic", desc = "Calls over 1 enemy squad member(s). Can be executed on enemies engaged in combat.", comment = "One version of the program led to a political crisis in the Middle East where it was used on an entire Arasaka tank division, causing them to surround a building where a critical peace negotiations were taking place.", },
[0x00000018EB045547] = { id = "Items.EMPOverloadProgram", name = "SHORT CIRCUIT", kind = "Quickhack", quality = "Uncommon", desc = "Non-Leathal. Deals moderate damage to the target. Very effective against drones, mechs, robots, and targets with a weakspot.", comment = "Overloads an enemy's microcircuits, producing a devastating electrical current of almost 2 million Volts.", },
[0x0000001C8EC9A541] = { id = "Items.EMPOverloadLvl2Program", name = "SHORT CIRCUIT", kind = "Quickhack", quality = "Rare", desc = "Non-Leathal. Deals moderate damage to the target. Very effective against drones, mechs, robots, and targets with a weakspot. Applies an EMP effect to the target for 4 sec.", comment = "Overloads an enemy's microcircuits, producing a devastating electrical current of almost 2 million Volts.", },
[0x0000001C4263A5DF] = { id = "Items.EMPOverloadLvl3Program", name = "SHORT CIRCUIT", kind = "Quickhack", quality = "Epic", desc = "Non-Leathal. Deals moderate damage to the target. Very effective against drones, mechs, robots, and targets with a weakspot. Applies an EMP effect to the target for 4 sec. Deals 30% extra damage to enemies below a High threat level.", comment = "Overloads an enemy's microcircuits, producing a devastating electrical current of almost 2 million Volts.", },
[0x0000001C48A6ACC6] = { id = "Items.EMPOverloadLvl4Program", name = "SHORT CIRCUIT", kind = "Quickhack", quality = "Legendary", desc = "Non-Leathal. Deals moderate damage to the target. Very effective against drones, mechs, robots, and targets with a weakspot. Applies an EMP effect to the target for 4 sec. Deals 30% extra damage to enemies below a High threat level. Passive While Equipped: Crit Hits with any weapon apply this quickhack's Tier 1 effect.", comment = "Overloads an enemy's microcircuits, producing a devastating electrical current of almost 2 million Volts.", },
[0x000000172E408D40] = { id = "Items.CommsNoiseProgram", name = "SONIC SHOCK", kind = "Quickhack", quality = "Uncommon", desc = "Deafens the target, Reducing their ability to detect enemy sounds.", comment = "Emits a high-pitched frequency inside the target's skull. Some say that if it lasts long enough it can induce a psychological break. One version of the program written by netrunners from the Glen is known as Banshee's Wail.", },
[0x0000001BEF9F454E] = { id = "Items.CommsNoiseLvl2Program", name = "SONIC SHOCK", kind = "Quickhack", quality = "Rare", desc = "Deafens the target, Reducing their ability to detect enemy sounds. Prevents target from communicating with their allies about your activity.", comment = "Emits a high-pitched frequency inside the target's skull. Some say that if it lasts long enough it can induce a psychological break. One version of the program written by netrunners from the Glen is known as Banshee's Wail.", },
[0x0000001B233545D0] = { id = "Items.CommsNoiseLvl3Program", name = "SONIC SHOCK", kind = "Quickhack", quality = "Epic", desc = "Deafens the target, Reducing their ability to detect enemy sounds. Prevents target from communicating with their allies about your activity. Excludes the target from their allies' audio and visual preception systems, Causing them to be completely ignored.", comment = "Emits a high-pitched frequency inside the target's skull. Some say that if it lasts long enough it can induce a psychological break. One version of the program written by netrunners from the Glen is known as Banshee's Wail.", },
[0x0000001B29F04CC9] = { id = "Items.CommsNoiseLvl4Program", name = "SONIC SHOCK", kind = "Quickhack", quality = "Legendary", desc = "Deafens the target, Reducing their ability to detect enemy sounds. Prevents target from communicating with their allies about your activity. Excludes the target from their allies' audio and visual preception systems, Causing them to be completely ignored.", comment = "Emits a high-pitched frequency inside the target's skull. Some say that if it lasts long enough it can induce a psychological break. One version of the program written by netrunners from the Glen is known as Banshee's Wail.", },
[0x0000001893D1E095] = { id = "Items.SuicideLvl3Program", name = "SUICIDE", kind = "Quickhack", quality = "Epic", desc = "Leathal. Forces target to commit suicide.", comment = "\"Suicide note not included.\" - Emmalicious, purported author of the first suicide program", },
[0x000000189914E98C] = { id = "Items.SuicideLvl4Program", name = "SUICIDE", kind = "Quickhack", quality = "Legendary", desc = "Leathal. Forces target to commit suicide. Passive While Equipped: Causing an enemy to panic reduces the RAM cost of your next Ultimate quickhack by 2.", comment = "\"Suicide note not included.\" - Emmalicious, purported author of the first suicide program", },
[0x0000001AAC2F28B0] = { id = "Items.BrainMeltLvl2Program", name = "SYNAPSE BURNOUT", kind = "Quickhack", quality = "Rare", desc = "Deals Moderate damage that scales higher based on how much health the target is missing.", comment = "Causes the brain to suffer from stroke-like symptoms: paresis, visual and speech disorders, and even loss of balance.", },
[0x0000001A6085282E] = { id = "Items.BrainMeltLvl3Program", name = "SYNAPSE BURNOUT", kind = "Quickhack", quality = "Epic", desc = "Deals Moderate damage that scales higher based on how much health the target is missing. Less effective against drones, mech and robots. If the target is defeated by this quickhack,they burst into flames, cuasing enemies within a 6-meter radius to panic.", comment = "Causes the brain to suffer from stroke-like symptoms: paresis, visual and speech disorders, and even loss of balance.", },
[0x0000001A6A402137] = { id = "Items.BrainMeltLvl4Program", name = "SYNAPSE BURNOUT", kind = "Quickhack", quality = "Legendary", desc = "Deals Moderate damage that scales higher based on how much health the target is missing. Less effective against drones, mech and robots. If the target is defeated by this quickhack,they burst into flames, cuasing enemies within a 6-meter radius to panic. Passive While Equiped: Defeating an enemy with any quickhack causes nearby enemies to panic.", comment = "Causes the brain to suffer from stroke-like symptoms: paresis, visual and speech disorders, and even loss of balance.", },
[0x0000001F51C1D85C] = { id = "Items.SystemCollapseLvl3Program", name = "SYSTEM RESET", kind = "Quickhack", quality = "Epic", desc = "Cripples a target's nervous system, causing them to lose consciousness. The affected target will not make any noise when passing out.", comment = "The magnum opus of a corporate netrunner who believed most problems could be solved by simply turning off whatever is giving you trouble.", },
[0x0000001F5B04D145] = { id = "Items.SystemCollapseLvl4Program", name = "SYSTEM RESET", kind = "Quickhack", quality = "Legendary", desc = "Cripples a target's nervous system, causing them to lose consciousness. The affected target will not make any noise when passing out. Passive while equipped: defeating an enemy reduces the RAM cost of the next quickhack.", comment = "The magnum opus of a corporate netrunner who believed most problems could be solved by simply turning off whatever is giving you trouble.", },
[0x0000001E40DFEBAA] = { id = "Items.WeaponMalfunctionProgram", name = "WEAPON GLITCH", kind = "Quickhack", quality = "Uncommon", desc = "Causes the target's weapon to malfunction, reducing its accuracy and disabling Smart tracking and obstavle penetration.", comment = "During the Unification War, Militech soldiers feared little more than the chance their weapons might jam in the heat of battle.", },
[0x0000002246D4890A] = { id = "Items.WeaponMalfunctionLvl2Program", name = "WEAPON GLITCH", kind = "Quickhack", quality = "Rare", desc = "Causes the target's weapon to malfunction, reducing its accuracy and disabling Smart tracking and obstavle penetration. Spreads to the nearest target within a radius.", comment = "During the Unification War, Militech soldiers feared little more than the chance their weapons might jam in the heat of battle.", },
[0x000000228A7E8994] = { id = "Items.WeaponMalfunctionLvl3Program", name = "WEAPON GLITCH", kind = "Quickhack", quality = "Epic", desc = "Causes the target's weapon to malfunction, reducing its accuracy and disabling Smart tracking and obstavle penetration. Spreads to the nearest target within a radius. Causes the target's weapon to explode, causing damage.", comment = "During the Unification War, Militech soldiers feared little more than the chance their weapons might jam in the heat of battle.", },
[0x0000002280BB808D] = { id = "Items.WeaponMalfunctionLvl4Program", name = "WEAPON GLITCH", kind = "Quickhack", quality = "Legendary", desc = "Causes the target's weapon to malfunction, reducing its accuracy and disabling Smart tracking and obstavle penetration. Spreads to the nearest target within a radius. Causes the target's weapon to explode, causing damage. Passive while equipped: enables the Weapons Jammer deamon during Breach Protocol.", comment = "During the Unification War, Militech soldiers feared little more than the chance their weapons might jam in the heat of battle.", },
[0x000000147B8A64FE] = { id = "Items.WhistleProgram", name = "WHISTLE", kind = "Quickhack", quality = "Uncommon", desc = "The affected target will enter a heightened state of alertness and move to your current position.", comment = "\"By the time they reach you, you're already gone. You know, I've always said advanced tech looks a lot like magic.\" - Cracker Jack", },
[0x000000189E150D77] = { id = "Items.WhistleLvl2Program", name = "WHISTLE", kind = "Quickhack", quality = "Rare", desc = "The target will no longer be in a state of alertness when moving to your current position.", comment = "\"By the time they reach you, you're already gone. You know, I've always said advanced tech looks a lot like magic.\" - Cracker Jack", },
