[0x0000001F94FA91AF] = { id = "Items.GrenadeExplodeLvl3Program", name = "DETONATE GRENADE", kind = "Quickhack", quality = "Epic", desc = "Forces a target to trigger a carried grenade. The type of explosion and its effects are based on the target's grenade type.", comment = "Especially popular with South American mercenaries, believing they were doing their enemies a favor by helping them \"go out with a bang.\"", },
